require('dotenv').config(); // <--- EN BA≈ûTA OLMALI
const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');
const path = require('path');

// Route Dosyalarƒ±
const categoryRoutes = require('./routes/categoryRoutes');
const productRoutes = require('./routes/productRoutes');
const restaurantRoutes = require('./routes/restaurantRoutes');
const authRoutes = require('./routes/authRoutes');
const orderRoutes = require('./routes/orderRoutes');
const aiRoutes = require('./routes/aiRoutes'); // <--- AI Rotasƒ±

const app = express();
const PORT = process.env.PORT || 5000;

// Middleware
app.use(cors()); // <--- Frontend ile ileti≈üim i√ßin ≈üart
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Statik Dosyalar (Resimler i√ßin)
app.use('/uploads', express.static(path.join(__dirname, 'uploads')));

// Route Tanƒ±mlarƒ±
app.use('/api/categories', categoryRoutes);
app.use('/api/products', productRoutes);
app.use('/api/restaurants', restaurantRoutes);
app.use('/api/auth', authRoutes);
app.use('/api/restaurants/:restaurantId/orders', orderRoutes);
app.use('/api/ai', aiRoutes); // <--- AI Endpoint'i

// MongoDB Baƒülantƒ±sƒ± ve Sunucu Ba≈ülatma
if (!process.env.MONGO_URI) {
  console.error('‚ùå HATA: MONGO_URI .env dosyasƒ±nda tanƒ±mlƒ± deƒüil!');
}

mongoose.connect(process.env.MONGO_URI, { dbName: process.env.MONGO_DB })
  .then(() => {
    console.log('‚úÖ MongoDB baƒülantƒ±sƒ± ba≈üarƒ±lƒ±');
    app.listen(PORT, () => {
      console.log(`üöÄ Sunucu http://localhost:${PORT} adresinde √ßalƒ±≈üƒ±yor`);
    });
  })
  .catch((err) => {
    console.error('‚ùå MongoDB baƒülantƒ± hatasƒ±:', err.message);
  });
