import { MessageSquare, Send, X } from 'lucide-react'; // Ä°kon paketini yÃ¼klemiÅŸsindir
import { useEffect, useRef, useState } from 'react';
import './AIChatWidget.css'; // AÅŸaÄŸÄ±da CSS'i vereceÄŸim

const AIChatWidget = ({ restaurantId, menuItems, onAddToCart }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Merhaba! ðŸ‘‹ Ben dijital garsonunuz. Ne yemek istersiniz? Size yardÄ±mcÄ± olabilirim.' }
  ]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Mesaj gelince en alta kaydÄ±r
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isOpen]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = input;
    setInput('');
    setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
    setIsTyping(true);

    try {
      // MenÃ¼ bilgisini basitleÅŸtirip gÃ¶nderiyoruz (token tasarrufu iÃ§in)
      const simplifiedMenu = menuItems.map(item => ({
        id: item._id,
        name: item.name,
        desc: item.description,
        price: item.price,
        category: item.category?.name || 'Genel'
      }));

      // Backend'e istek at
      // NOT: Backend hazÄ±r deÄŸilse burayÄ± simÃ¼le edebiliriz.
      /* const res = await axios.post('/api/ai/chat', { 
        message: userMessage, 
        menuContext: simplifiedMenu,
        restaurantId 
      });
      const aiResponseRaw = res.data.reply;
      */

      // --- SÄ°MÃœLASYON (Backend olmadan test etmek iÃ§in) ---
      // GerÃ§ekte burasÄ± backend'den gelecek
      let aiResponseRaw = "Bunu hemen hallediyorum.";
      if (userMessage.toLowerCase().includes("burger")) {
         // MenÃ¼den ilk burgeri bulup ekleyelim
         const burger = menuItems.find(i => i.name.toLowerCase().includes("burger"));
         if (burger) {
            aiResponseRaw = `Harika tercih! ðŸ” ${burger.name} sepetine eklendi. BaÅŸka bir arzun var mÄ±? {ADD:${burger._id}}`;
         } else {
            aiResponseRaw = "ÃœzgÃ¼nÃ¼m, ÅŸu an burger kalmamÄ±ÅŸ olabilir mi? MenÃ¼yÃ¼ kontrol ediyorum.";
         }
      } else {
         aiResponseRaw = "AnladÄ±m, menÃ¼den senin iÃ§in en gÃ¼zelini seÃ§elim. Biraz daha detay verir misin? AcÄ± sever misin?";
      }
      // ----------------------------------------------------

      // Aksiyon KontrolÃ¼: {ADD:URUN_ID} formatÄ±nÄ± yakala
      const actionRegex = /\{ADD:([a-zA-Z0-9]+)\}/;
      const match = aiResponseRaw.match(actionRegex);

      let displayMessage = aiResponseRaw;

      if (match) {
        const productId = match[1];
        // Mesajdan kodu temizle
        displayMessage = aiResponseRaw.replace(match[0], '');

        // ÃœrÃ¼nÃ¼ bul ve sepete ekleme fonksiyonunu Ã§alÄ±ÅŸtÄ±r
        const productToAdd = menuItems.find(p => p._id === productId);
        if (productToAdd) {
          onAddToCart(productToAdd); // MenuPage'den gelen fonksiyonu tetikle!
        }
      }

      setMessages(prev => [...prev, { role: 'assistant', content: displayMessage }]);

    } catch (error) {
      setMessages(prev => [...prev, { role: 'assistant', content: 'Åžu an baÄŸlantÄ±mda bir sorun var, garson Ã§aÄŸÄ±rabilir misiniz? ðŸ˜”' }]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <>
      {/* YÃ¼zen Sohbet Butonu */}
      <button 
        className={`ai-chat-fab ${isOpen ? 'hidden' : ''}`} 
        onClick={() => setIsOpen(true)}
      >
        <MessageSquare size={24} color="white" />
        <span className="ai-fab-text">Asistana Sor</span>
      </button>

      {/* Sohbet Penceresi */}
      <div className={`ai-chat-window ${isOpen ? 'open' : ''}`}>
        <div className="ai-header">
          <div className="ai-avatar">ðŸ¤–</div>
          <div className="ai-title">
            <h3>Marithen Asistan</h3>
            <span>Online</span>
          </div>
          <button className="ai-close" onClick={() => setIsOpen(false)}>
            <X size={20} />
          </button>
        </div>

        <div className="ai-messages">
          {messages.map((msg, idx) => (
            <div key={idx} className={`message ${msg.role}`}>
              <div className="message-bubble">
                {msg.content}
              </div>
            </div>
          ))}
          {isTyping && (
            <div className="message assistant">
              <div className="typing-indicator">
                <span></span><span></span><span></span>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <div className="ai-input-area">
          <input 
            type="text" 
            placeholder="Ã–rn: AcÄ±ktÄ±m, ne Ã¶nerirsin?" 
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          />
          <button onClick={handleSend} disabled={!input.trim()}>
            <Send size={18} />
          </button>
        </div>
      </div>
    </>
  );
};

export default AIChatWidget;
