import axios from 'axios';
import { Plus, QrCode, Save, Trash2, X } from 'lucide-react';
import React, { useEffect, useState } from 'react';
import './AdminPanel.css';

function AdminPanel() {
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('restaurant');
  const [qrCode, setQrCode] = useState(null);

  // Form states
  const [restaurantForm, setRestaurantForm] = useState({
    name: '',
    description: '',
    address: '',
    phone: ''
  });

  const [categoryForm, setCategoryForm] = useState({
    name: ''
  });

  const [productForm, setProductForm] = useState({
    name: '',
    price: '',
    description: '',
    category: ''
  });

  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);

      // Önce demo restaurant oluştur veya al
      const demoRes = await axios.post('/api/restaurants/demo');
      const demoRestaurant = demoRes.data.restaurant;

      setRestaurant(demoRestaurant);
      setRestaurantForm({
        name: demoRestaurant.name,
        description: demoRestaurant.description,
        address: demoRestaurant.address,
        phone: demoRestaurant.phone
      });

      // Kategorileri al
      const categoriesRes = await axios.get('/api/categories');
      setCategories(categoriesRes.data);

      // Ürünleri al
      const productsRes = await axios.get('/api/products');
      setProducts(productsRes.data);

    } catch (error) {
      console.error('Veri yükleme hatası:', error);
      // Hata durumunda demo data oluştur
      createDemoData();
    } finally {
      setLoading(false);
    }
  };

  const createDemoData = () => {
    setRestaurant({
      _id: 'demo-restaurant-id',
      name: 'Demo Restaurant',
      description: 'Lezzetli yemekler',
      address: 'İstanbul, Kadıköy',
      phone: '0216 555 1234'
    });
    setRestaurantForm({
      name: 'Demo Restaurant',
      description: 'Lezzetli yemekler',
      address: 'İstanbul, Kadıköy',
      phone: '0216 555 1234'
    });
  };

  // Restaurant güncelleme
  const handleRestaurantUpdate = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      Object.keys(restaurantForm).forEach(key => {
        formData.append(key, restaurantForm[key]);
      });

      if (selectedImage) {
        formData.append('logo', selectedImage);
      }

      const response = await axios.put(`/api/restaurants/${restaurant._id}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      if (response.data.success) {
        alert('Restaurant bilgileri güncellendi!');
        // Restaurant state'ini güncelle
        setRestaurant(response.data.restaurant);
        fetchData();
      } else {
        alert('Güncelleme başarısız!');
      }
    } catch (error) {
      console.error('Güncelleme hatası:', error);
      alert('Güncelleme hatası: ' + (error.response?.data?.error || error.message));
    }
  };

  // Kategori ekleme
  const handleCategorySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/categories', categoryForm);
      setCategoryForm({ name: '' });
      setShowCategoryModal(false);
      fetchData();
      alert('Kategori eklendi!');
    } catch (error) {
      alert('Kategori ekleme hatası: ' + error.message);
    }
  };

  // Ürün ekleme
  const handleProductSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      Object.keys(productForm).forEach(key => {
        formData.append(key, productForm[key]);
      });

      if (selectedImage) {
        formData.append('image', selectedImage);
      }

      await axios.post('/api/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setProductForm({ name: '', price: '', description: '', category: '' });
      setSelectedImage(null);
      setShowProductModal(false);
      fetchData();
      alert('Ürün eklendi!');
    } catch (error) {
      alert('Ürün ekleme hatası: ' + error.message);
    }
  };

  // QR kod oluşturma
  const generateQRCode = async () => {
    try {
      const response = await axios.post(`/api/restaurants/${restaurant._id}/qr-code`);
      setQrCode(response.data.qrCode);
    } catch (error) {
      alert('QR kod oluşturma hatası: ' + error.message);
    }
  };

  // Kategori silme
  const deleteCategory = async (categoryId) => {
    if (window.confirm('Bu kategoriyi silmek istediğinizden emin misiniz?')) {
      try {
        await axios.delete(`/api/categories/${categoryId}`);
        fetchData();
        alert('Kategori silindi!');
      } catch (error) {
        alert('Silme hatası: ' + error.message);
      }
    }
  };

  // Ürün silme
  const deleteProduct = async (productId) => {
    if (window.confirm('Bu ürünü silmek istediğinizden emin misiniz?')) {
      try {
        await axios.delete(`/api/products/${productId}`);
        fetchData();
        alert('Ürün silindi!');
      } catch (error) {
        alert('Silme hatası: ' + error.message);
      }
    }
  };

  if (loading) {
    return <div className="loading">Yükleniyor...</div>;
  }

  return (
    <div className="admin-panel">
      <div className="container">
        <h1>Restaurant Yönetim Paneli</h1>

        {/* Tab Navigation */}
        <div className="tab-navigation">
          <button 
            className={`tab-btn ${activeTab === 'restaurant' ? 'active' : ''}`}
            onClick={() => setActiveTab('restaurant')}
          >
            Restaurant Bilgileri
          </button>
          <button 
            className={`tab-btn ${activeTab === 'categories' ? 'active' : ''}`}
            onClick={() => setActiveTab('categories')}
          >
            Kategoriler
          </button>
          <button 
            className={`tab-btn ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            Ürünler
          </button>
          <button 
            className={`tab-btn ${activeTab === 'qr' ? 'active' : ''}`}
            onClick={() => setActiveTab('qr')}
          >
            QR Kod
          </button>
        </div>

        {/* Restaurant Bilgileri Tab */}
        {activeTab === 'restaurant' && (
          <div className="tab-content">
            <h2>Restaurant Bilgileri</h2>
            <form onSubmit={handleRestaurantUpdate} className="form">
              <div className="form-group">
                <label>Restaurant Adı</label>
                <input
                  type="text"
                  value={restaurantForm.name}
                  onChange={(e) => setRestaurantForm({...restaurantForm, name: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label>Açıklama</label>
                <textarea
                  value={restaurantForm.description}
                  onChange={(e) => setRestaurantForm({...restaurantForm, description: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>Adres</label>
                <input
                  type="text"
                  value={restaurantForm.address}
                  onChange={(e) => setRestaurantForm({...restaurantForm, address: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>Telefon</label>
                <input
                  type="text"
                  value={restaurantForm.phone}
                  onChange={(e) => setRestaurantForm({...restaurantForm, phone: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>Logo</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setSelectedImage(e.target.files[0])}
                />
              </div>

              <button type="submit" className="btn btn-primary">
                <Save size={16} />
                Güncelle
              </button>
            </form>
          </div>
        )}

        {/* Kategoriler Tab */}
        {activeTab === 'categories' && (
          <div className="tab-content">
            <div className="section-header">
              <h2>Kategoriler</h2>
              <button 
                className="btn btn-primary"
                onClick={() => setShowCategoryModal(true)}
              >
                <Plus size={16} />
                Yeni Kategori
              </button>
            </div>

            <div className="categories-grid">
              {categories.map(category => (
                <div key={category._id} className="category-card">
                  <h3>{category.name}</h3>
                  <div className="card-actions">
                    <button className="btn-icon" onClick={() => deleteCategory(category._id)}>
                      <Trash2 size={16} />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Ürünler Tab */}
        {activeTab === 'products' && (
          <div className="tab-content">
            <div className="section-header">
              <h2>Ürünler</h2>
              <button 
                className="btn btn-primary"
                onClick={() => setShowProductModal(true)}
              >
                <Plus size={16} />
                Yeni Ürün
              </button>
            </div>

            <div className="products-grid">
              {products.map(product => (
                <div key={product._id} className="product-card">
                  {product.image && (
                    <img src={product.image} alt={product.name} />
                  )}
                  <div className="product-info">
                    <h3>{product.name}</h3>
                    <p>{product.description}</p>
                    <p className="price">{product.price} ₺</p>
                  </div>
                  <div className="card-actions">
                    <button className="btn-icon" onClick={() => deleteProduct(product._id)}>
                      <Trash2 size={16} />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* QR Kod Tab */}
        {activeTab === 'qr' && (
          <div className="tab-content">
            <h2>QR Kod</h2>
            <p>Müşterilerin menünüzü görebilmesi için QR kod oluşturun.</p>

            <button className="btn btn-primary" onClick={generateQRCode}>
              <QrCode size={16} />
              QR Kod Oluştur
            </button>

            {qrCode && (
              <div className="qr-code-section">
                <h3>QR Kodunuz</h3>
                <img src={qrCode} alt="QR Code" className="qr-code" />
                <p>Bu QR kodu müşterilerinize gösterin.</p>
              </div>
            )}
          </div>
        )}

        {/* Kategori Modal */}
        {showCategoryModal && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Yeni Kategori</h3>
                <button onClick={() => setShowCategoryModal(false)}>
                  <X size={20} />
                </button>
              </div>
              <form onSubmit={handleCategorySubmit}>
                <div className="form-group">
                  <label>Kategori Adı</label>
                  <input
                    type="text"
                    value={categoryForm.name}
                    onChange={(e) => setCategoryForm({name: e.target.value})}
                    required
                  />
                </div>
                <div className="modal-actions">
                  <button type="button" onClick={() => setShowCategoryModal(false)}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Ekle
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Ürün Modal */}
        {showProductModal && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Yeni Ürün</h3>
                <button onClick={() => setShowProductModal(false)}>
                  <X size={20} />
                </button>
              </div>
              <form onSubmit={handleProductSubmit}>
                <div className="form-group">
                  <label>Ürün Adı</label>
                  <input
                    type="text"
                    value={productForm.name}
                    onChange={(e) => setProductForm({...productForm, name: e.target.value})}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Fiyat</label>
                  <input
                    type="number"
                    step="0.01"
                    value={productForm.price}
                    onChange={(e) => setProductForm({...productForm, price: e.target.value})}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Açıklama</label>
                  <textarea
                    value={productForm.description}
                    onChange={(e) => setProductForm({...productForm, description: e.target.value})}
                  />
                </div>

                <div className="form-group">
                  <label>Kategori</label>
                  <select
                    value={productForm.category}
                    onChange={(e) => setProductForm({...productForm, category: e.target.value})}
                    required
                  >
                    <option value="">Kategori Seçin</option>
                    {categories.map(category => (
                      <option key={category._id} value={category._id}>
                        {category.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Ürün Resmi</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setSelectedImage(e.target.files[0])}
                  />
                </div>

                <div className="modal-actions">
                  <button type="button" onClick={() => setShowProductModal(false)}>
                    İptal
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Ekle
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPanel;
