import axios from 'axios';
import { Check, Plus, QrCode, Save, Trash2, X, AlertCircle, RefreshCw, Activity } from 'lucide-react';
import React, { useEffect, useState } from 'react';
import './AdminPanel.css';

function AdminPanel() {
  // Ã–neri kategorileri iÃ§in yerel yedek (backend cevap vermezse)
  const PREDEFINED_FALLBACK = [
    { _id: 'fallback-Ana Yemekler', name: 'Ana Yemekler', icon: 'ðŸ–' },
    { _id: 'fallback-Ã‡orbalar', name: 'Ã‡orbalar', icon: 'ðŸ¥£' },
    { _id: 'fallback-Salatalar', name: 'Salatalar', icon: 'ðŸ¥—' },
    { _id: 'fallback-Pizzalar', name: 'Pizzalar', icon: 'ðŸ•' },
    { _id: 'fallback-Hamburgerler', name: 'Hamburgerler', icon: 'ðŸ”' },
    { _id: 'fallback-Kebap', name: 'Kebap', icon: 'ðŸ¥™' },
    { _id: 'fallback-TatlÄ±lar', name: 'TatlÄ±lar', icon: 'ðŸ°' },
    { _id: 'fallback-Ä°Ã§ecekler', name: 'Ä°Ã§ecekler', icon: 'ðŸ¥¤' },
    { _id: 'fallback-KahvaltÄ±', name: 'KahvaltÄ±', icon: 'ðŸ³' },
    { _id: 'fallback-Ara SÄ±caklar', name: 'Ara SÄ±caklar', icon: 'ðŸŸ' },
    { _id: 'fallback-Deniz ÃœrÃ¼nleri', name: 'Deniz ÃœrÃ¼nleri', icon: 'ðŸŸ' },
    { _id: 'fallback-Vejetaryen', name: 'Vejetaryen', icon: 'ðŸ¥¬' },
    { _id: 'fallback-Ã‡ocuk MenÃ¼sÃ¼', name: 'Ã‡ocuk MenÃ¼sÃ¼', icon: 'ðŸ‘¶' },
    { _id: 'fallback-Ã–zel GÃ¼nler', name: 'Ã–zel GÃ¼nler', icon: 'ðŸŽ‰' },
    { _id: 'fallback-Geleneksel', name: 'Geleneksel', icon: 'ðŸº' },
  ];
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState([]);
  const [predefinedCategories, setPredefinedCategories] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('restaurant');
  const [modalActiveTab, setModalActiveTab] = useState('predefined');
  const [qrCode, setQrCode] = useState(null);

  // Form states
  const [restaurantForm, setRestaurantForm] = useState({
    name: '',
    description: '',
    address: '',
    phone: ''
  });

  const [categoryForm, setCategoryForm] = useState({
    name: '',
    icon: 'ðŸ½ï¸'
  });

  const [productForm, setProductForm] = useState({
    name: '',
    price: '',
    description: '',
    category: ''
  });

  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedCoverImage, setSelectedCoverImage] = useState(null);

  // AI Service states
  const [aiStatus, setAiStatus] = useState(null);
  const [aiStatusLoading, setAiStatusLoading] = useState(false);
  const [aiErrors, setAiErrors] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  // AI tab aÃ§Ä±ldÄ±ÄŸÄ±nda durumu kontrol et
  useEffect(() => {
    if (activeTab === 'ai') {
      checkAIStatus();
    }
  }, [activeTab]);

  const fetchData = async () => {
    try {
      setLoading(true);

      // Ã–nce demo restaurant oluÅŸtur veya al
      const demoRes = await axios.post('/api/restaurants/demo');
      const demoRestaurant = demoRes.data.restaurant;

      setRestaurant(demoRestaurant);
      setRestaurantForm({
        name: demoRestaurant.name,
        description: demoRestaurant.description,
        address: demoRestaurant.address,
        phone: demoRestaurant.phone
      });

      // Ã–neri kategorilerini al (backend boÅŸ/kapalÄ±ysa yerel yedek kullan)
      try {
        const predefinedRes = await axios.get('/api/categories/predefined');
        if (Array.isArray(predefinedRes.data) && predefinedRes.data.length > 0) {
          setPredefinedCategories(predefinedRes.data);
        } else {
          setPredefinedCategories(PREDEFINED_FALLBACK);
        }
      } catch (error) {
        console.log('Ã–neri kategorileri yÃ¼klenemedi, yerel yedek kullanÄ±lacak:', error.message);
        setPredefinedCategories(PREDEFINED_FALLBACK);
      }

      // TÃ¼m kategorileri al
      const categoriesRes = await axios.get('/api/categories');
      setCategories(categoriesRes.data);

      // SeÃ§ili kategorileri ayarla (mevcut kategoriler)
      setSelectedCategories(categoriesRes.data.map(cat => cat._id));

      // ÃœrÃ¼nleri al
      const productsRes = await axios.get('/api/products');
      setProducts(productsRes.data);

    } catch (error) {
      console.error('Veri yÃ¼kleme hatasÄ±:', error);
      // Hata durumunda basit demo data oluÅŸtur
      setRestaurant({
        _id: 'temp-id',
        name: 'Demo Restaurant',
        description: 'Lezzetli yemekler',
        address: 'Ä°stanbul, KadÄ±kÃ¶y',
        phone: '0216 555 1234'
      });
      setRestaurantForm({
        name: 'Demo Restaurant',
        description: 'Lezzetli yemekler',
        address: 'Ä°stanbul, KadÄ±kÃ¶y',
        phone: '0216 555 1234'
      });
      setCategories([]);
      setPredefinedCategories(PREDEFINED_FALLBACK);
      setSelectedCategories([]);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };



  // Restaurant gÃ¼ncelleme
  const handleRestaurantUpdate = async (e) => {
    e.preventDefault();

    // Restaurant ID kontrolÃ¼
    if (!restaurant || !restaurant._id || restaurant._id === 'temp-id') {
      alert('Ã–nce demo restaurant oluÅŸturun!');
      return;
    }

    try {
      const formData = new FormData();
      Object.keys(restaurantForm).forEach(key => {
        formData.append(key, restaurantForm[key]);
      });

      if (selectedImage) {
        formData.append('logo', selectedImage);
      }

      if (selectedCoverImage) {
        formData.append('coverImage', selectedCoverImage);
      }

      const response = await axios.put(`/api/restaurants/${restaurant._id}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      if (response.data.success) {
        alert('Restaurant bilgileri gÃ¼ncellendi!');
        // Restaurant state'ini gÃ¼ncelle
        setRestaurant(response.data.restaurant);
        // Form state'ini de gÃ¼ncelle
        setRestaurantForm({
          name: response.data.restaurant.name,
          description: response.data.restaurant.description,
          address: response.data.restaurant.address,
          phone: response.data.restaurant.phone
        });
        // SeÃ§ili resimleri temizle
        setSelectedImage(null);
        setSelectedCoverImage(null);
      } else {
        alert('GÃ¼ncelleme baÅŸarÄ±sÄ±z!');
      }
    } catch (error) {
      console.error('GÃ¼ncelleme hatasÄ±:', error);
      alert('GÃ¼ncelleme hatasÄ±: ' + (error.response?.data?.error || error.message));
    }
  };

  // HazÄ±r kategorileri baÅŸlat
  const initializePredefinedCategories = async () => {
    try {
      await axios.post('/api/categories/initialize-predefined');
      // Kategorileri yeniden yÃ¼kle
      const predefinedRes = await axios.get('/api/categories/predefined');
      setPredefinedCategories(predefinedRes.data);

      const categoriesRes = await axios.get('/api/categories');
      setCategories(categoriesRes.data);
      setSelectedCategories(categoriesRes.data.map(cat => cat._id));

      alert('HazÄ±r kategoriler baÅŸarÄ±yla oluÅŸturuldu!');
    } catch (error) {
      console.error('HazÄ±r kategoriler oluÅŸturma hatasÄ±:', error);
      alert('HazÄ±r kategoriler oluÅŸturulamadÄ±: ' + (error.response?.data?.error || error.message));
    }
  };

  // Kategori seÃ§imi
  const toggleCategorySelection = (categoryId) => {
    setSelectedCategories(prev => {
      if (prev.includes(categoryId)) {
        return prev.filter(id => id !== categoryId);
      } else {
        return [...prev, categoryId];
      }
    });
  };

  // SeÃ§ili kategorileri kaydet
  const saveSelectedCategories = async () => {
    try {
      // TÃ¼m kategorileri yeniden yÃ¼kle (seÃ§ili olanlar zaten dahil)
      const categoriesRes = await axios.get('/api/categories');
      setCategories(categoriesRes.data);
      setSelectedCategories(categoriesRes.data.map(cat => cat._id));
      alert('Kategori seÃ§imleri kaydedildi!');
    } catch (error) {
      console.error('Kategori kaydetme hatasÄ±:', error);
      alert('Kategori kaydetme hatasÄ±: ' + error.message);
    }
  };

  // Kategori ekleme
  const handleCategorySubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/categories', categoryForm);
