import axios from 'axios';
import { Minus, Plus, RefreshCw, Search, ShoppingCart, Trash } from 'lucide-react';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './MenuPage.css';

function MenuPage() {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [cart, setCart] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isOrderModalOpen, setIsOrderModalOpen] = useState(false);
  const [orderTable, setOrderTable] = useState('');
  const [orderNote, setOrderNote] = useState('');
  const [ordering, setOrdering] = useState(false);

  // AI √ñnerileri State
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    if (restaurantId) fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [restaurantId]);

  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) fetchData(true);
    };
    const handleFocus = () => {
      fetchData(true);
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('focus', handleFocus);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('focus', handleFocus);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [restaurantId]);

  const fetchData = async (forceRefresh = false) => {
    try {
      if (!forceRefresh) setLoading(true);

      const timestamp = forceRefresh ? `?t=${Date.now()}` : '';

      const [resData, catData, prodData] = await Promise.all([
        axios.get(`/api/restaurants/${restaurantId}${timestamp}`),
        axios.get(`/api/categories${timestamp}`),
        axios.get(`/api/products${timestamp}`)
      ]);

      setRestaurant(resData.data);
      setCategories(catData.data);
      setProducts(prodData.data);

      // AI ƒ∞steƒüi
      try {
        const customerData = {
          customer_id: 'musteri_101', 
          context: { time_of_day: 'evening' }
        };
        const recRes = await axios.post(
          `/api/ai/customer-recommendation/${restaurantId}`,
          customerData
        );
        setRecommendations(recRes.data);
      } catch (aiError) {
        console.error("AI √ñnerileri alƒ±namadƒ±:", aiError.message);
        setRecommendations([]);
      }

    } catch (error) {
      console.error('Veri y√ºkleme hatasƒ±:', error);
    } finally {
      setLoading(false);
    }
  };

  // Cart Helper Functions
  useEffect(() => {
    try {
      const stored = localStorage.getItem('qrmenu_cart');
      if (stored) setCart(JSON.parse(stored));
    } catch (_) {}
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('qrmenu_cart', JSON.stringify(cart));
    } catch (_) {}
  }, [cart]);

  const getProductId = (product) => product?._id;
  const getCartItemQuantity = (productId) => cart.find(ci => ci.productId === productId)?.quantity || 0;

  const addToCart = (product) => {
    const productId = getProductId(product);
    if (!productId) return;
    setCart(prev => {
      const existing = prev.find(ci => ci.productId === productId);
      if (existing) {
        return prev.map(ci => ci.productId === productId ? { ...ci, quantity: ci.quantity + 1 } : ci);
      }
      return [
        ...prev,
        { productId, name: product.name, price: Number(product.price) || 0, image: product.image, quantity: 1 }
      ];
    });
  };

  const incrementItem = (productId) => setCart(prev => prev.map(ci => ci.productId === productId ? { ...ci, quantity: ci.quantity + 1 } : ci));

  const decrementItem = (productId) => setCart(prev => prev.flatMap(ci => {
    if (ci.productId !== productId) return [ci];
    const nextQty = ci.quantity - 1;
    return nextQty > 0 ? [{ ...ci, quantity: nextQty }] : [];
  }));

  const removeItem = (productId) => setCart(prev => prev.filter(ci => ci.productId !== productId));
  const clearCart = () => setCart([]);

  const cartCount = cart.reduce((sum, ci) => sum + ci.quantity, 0);
  const cartTotal = cart.reduce((sum, ci) => sum + ci.quantity * (Number(ci.price) || 0), 0);

  const submitOrder = async () => {
    try {
      if (cart.length === 0) return;
      setOrdering(true);
      const payload = {
        table: orderTable,
        note: orderNote,
        items: cart.map(ci => ({ productId: ci.productId, name: ci.name, price: ci.price, quantity: ci.quantity }))
      };
      await axios.post(`/api/restaurants/${restaurantId}/orders`, payload);
      setOrdering(false);
      setIsOrderModalOpen(false);
      setIsCartOpen(false);
      clearCart();
      alert('Sipari≈üiniz alƒ±ndƒ±! Afiyet olsun.');
    } catch (e) {
      setOrdering(false);
      alert('Sipari≈ü g√∂nderilemedi: ' + (e.response?.data?.error || e.message));
    }
  };

  const getProductCategoryId = (product) => {
    if (!product || !product.category) return undefined;
    if (typeof product.category === 'string') return product.category;
    return product.category._id;
  };

  const productsMatchingSearch = products.filter(product => {
    const nameMatches = product.name?.toLowerCase().includes(searchTerm.toLowerCase());
    const descMatches = product.description?.toLowerCase().includes(searchTerm.toLowerCase());
    return Boolean(nameMatches || descMatches);
  });

  if (loading) {
    return (
      <div className="container" style={{ display: 'flex', justifyContent: 'center', paddingTop: '50px' }}>
        <div className="loading-spinner">üçΩÔ∏è Men√º Y√ºkleniyor...</div>
      </div>
    );
  }

  if (!restaurant) {
    return <div className="container"><div className="error">Restaurant bulunamadƒ±!</div></div>;
  }

  // Gruplanmƒ±≈ü √ºr√ºnleri hazƒ±rlama
  const renderProductList = (productList) => (
    <div className="products-grid">
      {productList.map(product => (
        <ProductCard
          key={product._id}
          product={product}
          quantity={getCartItemQuantity(product._id)}
          onAdd={() => addToCart(product)}
          onIncrement={() => incrementItem(product._id)}
          onDecrement={() => decrementItem(product._id)}
        />
      ))}
    </div>
  );

  return (
    <div className="menu-page">
      <div className="container">

        {/* HEADER */}
        <div className="restaurant-info">
          {restaurant.coverImage && (
            <div className="restaurant-cover">
              <img src={restaurant.coverImage} alt={restaurant.name} className="restaurant-cover-image" />
            </div>
          )}
          <div className="restaurant-details">
            <div className="restaurant-header">
              {restaurant.logo && (
                <img src={restaurant.logo} alt={restaurant.name} className="restaurant-logo" />
              )}
              <div className="restaurant-title">
                <h1>{restaurant.name}</h1>
                {restaurant.description && <p>{restaurant.description}</p>}
                {restaurant.address && <p>{restaurant.address}</p>}
              </div>
            </div>
          </div>
        </div>

        {/* SEARCH & FILTER */}
        <div className="search-filter">
          <div className="search-box">
            <Search size={18} color="#999" />
            <input
              type="text"
              placeholder="Men√ºde ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <button className="refresh-btn" onClick={() => fetchData(true)}>
            <RefreshCw size={18} />
          </button>
        </div>

        {/* CATEGORY CHIPS */}
        <div className="category-chips">
          <button
            className={`chip ${selectedCategory === 'all' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('all')}
          >
            T√ºm√º
          </button>
          {categories.map(category => (
            <button
              key={category._id}
              className={`chip ${selectedCategory === category._id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category._id)}
            >
              {category.icon && <span className="chip-icon">{category.icon}</span>}
              {category.name}
            </button>
          ))}
        </div>

        {/* AI RECOMMENDATIONS */}
        {recommendations.length > 0 && searchTerm === '' && selectedCategory === 'all' && (
          <div className="recommendations-section">
            <h2 className="category-title">‚ú® Sizin ƒ∞√ßin √ñnerilenler</h2>
            {renderProductList(recommendations)}
          </div>
        )}

        {/* MAIN PRODUCT LIST */}
        <div className="category-section">
          {selectedCategory === 'all' ? (
            productsMatchingSearch.length === 0 ? (
              <div className="no-products"><p>√úr√ºn bulunamadƒ±.</p></div>
            ) : (
              renderProductList(productsMatchingSearch)
            )
          ) : (
            (() => {
              const activeCategory = categories.find(c => c._id === selectedCategory);
              const activeProducts = productsMatchingSearch.filter(p => getProductCategoryId(p) === selectedCategory);
              return (
                <>
                  {activeCategory && (
                    <h2 className="category-title">
                      {activeCategory.icon} {activeCategory.name}
                    </h2>
                  )}
                  {activeProducts.length === 0 ? (
                    <p>Bu kategoride √ºr√ºn yok.</p>
                  ) : (
                    renderProductList(activeProducts)
                  )}
                </>
              );
            })()
          )}
        </div>

        {/* FLOATING CART BUTTON */}
        {cartCount > 0 && (
          <button className="cart-fab" onClick={() => setIsCartOpen(true)}>
            <div style={{display:'flex', alignItems:'center', gap: '8px'}}>
                <ShoppingCart size={20} />
                <span>Sepetim ({cartCount})</span>
            </div>
            <span className="cart-fab-total">{cartTotal.toFixed(2)} ‚Ç∫</span>
          </button>
        )}
      </div>

      {/* CART DRAWER */}
      {isCartOpen && (
        <div className="cart-drawer-overlay" onClick={() => setIsCartOpen(false)}>
          <div className="cart-drawer" onClick={(e) => e.stopPropagation()}>
            <div className="cart-header">
              <h3>Sepetim</h3>
              <button className="remove-btn" style={{marginLeft:0, color:'#333'}} onClick={() => setIsCartOpen(false)}>Kapat</button>
            </div>
            {cart.length === 0 ? (
              <div className="cart-empty" style={{padding:20, textAlign:'center'}}>Sepetiniz bo≈ü.</div>
            ) : (
              <div className="cart-items">
                {cart.map(item => (
                  <div key={item.productId} className="cart-item">
                    {item.image && <img className="cart-item-image" src={item.image} alt={item.name} />}
                    <div className="cart-item-info">
                      <div className="cart-item-name">{item.name}</div>
                      <div className="cart-item-price">{item.price.toFixed(2)} ‚Ç∫</div>
                      <div className="cart-qty-controls">
                        <button onClick={() => decrementItem(item.productId)} className="qty-btn"><Minus size={14} /></button>
                        <span className="qty">{item.quantity}</span>
                        <button onClick={() => incrementItem(item.productId)} className="qty-btn"><Plus size={14} /></button>
                        <button onClick={() => removeItem(item.productId)} className="remove-btn"><Trash size={16} /></button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
            <div className="cart-footer">
              <div className="cart-total">
                <span>Toplam</span>
                <strong>{cartTotal.toFixed(2)} ‚Ç∫</strong>
              </div>
              <div className="cart-actions">
                <button className="btn-secondary" onClick={clearCart} disabled={cart.length === 0}>Temizle</button>
                <button className="btn-primary" onClick={() => setIsOrderModalOpen(true)} disabled={cart.length === 0}>Sipari≈üi Tamamla</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* ORDER MODAL */}
      {isOrderModalOpen && (
        <div className="order-modal-overlay" onClick={() => setIsOrderModalOpen(false)}>
          <div className="order-modal" onClick={(e) => e.stopPropagation()}>
            <h3 style={{marginTop:0}}>Sipari≈üi Onayla</h3>
            <div className="order-form-group">
              <label>Masa No / ƒ∞sim</label>
              <input type="text" value={orderTable} onChange={(e) => setOrderTable(e.target.value)} placeholder="√ñrn: Masa 5" />
            </div>
            <div className="order-form-group">
              <label>Sipari≈ü Notu</label>
              <textarea value={orderNote} onChange={(e) => setOrderNote(e.target.value)} placeholder="Varsa √∂zel isteƒüiniz..." rows={3}></textarea>
            </div>
            <div className="order-summary" style={{marginBottom: 20, fontSize:'1.1rem', fontWeight:'bold', textAlign:'right'}}>
              <span>Tutar: </span>
              <span style={{color:'var(--primary-color)'}}>{cartTotal.toFixed(2)} ‚Ç∫</span>
            </div>
            <div className="order-modal-actions">
              <button className="btn-secondary" onClick={() => setIsOrderModalOpen(false)}>ƒ∞ptal</button>
              <button className="btn-primary" onClick={submitOrder} disabled={ordering}>
                {ordering ? 'G√∂nderiliyor...' : 'Sipari≈üi G√∂nder'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// √úr√ºn Kartƒ± Bile≈üeni (Deƒüi≈ümedi, sadece sƒ±nƒ±flar CSS ile e≈üle≈üti)
function ProductCard({ product, quantity = 0, onAdd, onIncrement, onDecrement }) {
  return (
    <div className="product-card">
      <div className="product-image">
        {product.image ? (
            <img src={product.image} alt={product.name} />
        ) : (
            <div style={{width:'100%', height:'100%', background:'#eee', display:'flex', alignItems:'center', justifyContent:'center'}}>üçî</div>
        )}
      </div>
      <div className="product-info">
        <div>
            <h3>{product.name}</h3>
            {product.description && <p>{product.description}</p>}
        </div>
        <div style={{display:'flex', justifyContent:'space-between', alignItems:'flex-end', marginTop:'auto'}}>
            <div className="product-price">
            <span className="price">{Number(product.price).toFixed(2)} ‚Ç∫</span>
            </div>
            <div className="product-actions">
            {quantity > 0 ? (
                <div className="qty-inline">
                <button className="qty-btn" onClick={onDecrement}><Minus size={14} /></button>
                <span className="qty">{quantity}</span>
                <button className="qty-btn" onClick={onIncrement}><Plus size={14} /></button>
                </div>
            ) : (
                <button className="add-btn btn-primary" onClick={onAdd}>Ekle</button>
            )}
            </div>
        </div>
      </div>
    </div>
  );
}

export default MenuPage;
