import axios from 'axios';
import { Minus, Plus, RefreshCw, Search, ShoppingCart, Trash } from 'lucide-react';
import { useEffect, useState } from 'react'; // React importunu dÃ¼zelttim (useState ekliydi)
import { useParams } from 'react-router-dom';
import './MenuPage.css';

function MenuPage() {
Â  const { restaurantId } = useParams();
Â  const [restaurant, setRestaurant] = useState(null);
Â  const [categories, setCategories] = useState([]);
Â  const [products, setProducts] = useState([]);
Â  const [loading, setLoading] = useState(true);
Â  const [searchTerm, setSearchTerm] = useState('');
Â  const [selectedCategory, setSelectedCategory] = useState('all');
Â  const [cart, setCart] = useState([]); // { productId, name, price, image, quantity }
Â  const [isCartOpen, setIsCartOpen] = useState(false);
Â  const [isOrderModalOpen, setIsOrderModalOpen] = useState(false);
Â  const [orderTable, setOrderTable] = useState('');
Â  const [orderNote, setOrderNote] = useState('');
Â  const [ordering, setOrdering] = useState(false);

  // --- YENÄ° ADIM 1: AI Ã–nerilerini Saklamak Ä°Ã§in Yeni State ---
  const [recommendations, setRecommendations] = useState([]);

Â  useEffect(() => {
Â  Â  fetchData();
Â  }, [restaurantId]);

Â  // Sayfa gÃ¶rÃ¼nÃ¼rlÃ¼ÄŸÃ¼ deÄŸiÅŸtiÄŸinde verileri yenile (sayfa yenilendiÄŸinde)
Â  useEffect(() => {
Â  Â  const handleVisibilityChange = () => {
Â  Â  Â  if (!document.hidden) {
Â  Â  Â  Â  fetchData(true); // Force refresh
Â  Â  Â  }
Â  Â  };

Â  Â  const handleFocus = () => {
Â  Â  Â  fetchData(true); // Force refresh
Â  Â  };

Â  Â  document.addEventListener('visibilitychange', handleVisibilityChange);
Â  Â  window.addEventListener('focus', handleFocus);

Â  Â  return () => {
Â  Â  Â  document.removeEventListener('visibilitychange', handleVisibilityChange);
Â  Â  Â  window.removeEventListener('focus', handleFocus);
Â  Â  };
Â  }, [restaurantId]);

  // --- YENÄ° ADIM 2: fetchData Fonksiyonunu AI Ä°steÄŸi Ä°Ã§erecek Åekilde GÃ¼ncelle ---
Â  const fetchData = async (forceRefresh = false) => {
Â  Â  try {
Â  Â  Â  setLoading(true);
Â  Â  Â  
Â  Â  Â  // Cache-busting iÃ§in timestamp ekle
Â  Â  Â  const timestamp = forceRefresh ? `?t=${Date.now()}` : '';
Â  Â  Â  
Â  Â  Â  // Restaurant bilgilerini al
Â  Â  Â  const restaurantRes = await axios.get(`/api/restaurants/${restaurantId}${timestamp}`);
Â  Â  Â  setRestaurant(restaurantRes.data);
Â  Â  Â  
Â  Â  Â  // Kategorileri al (menÃ¼de gÃ¶stermek iÃ§in isim ve ikonlarÄ± kullanacaÄŸÄ±z)
Â  Â  Â  const categoriesRes = await axios.get(`/api/categories${timestamp}`);
Â  Â  Â  setCategories(categoriesRes.data);
Â  Â  Â  
Â  Â  Â  // ÃœrÃ¼nleri al
Â  Â  Â  const productsRes = await axios.get(`/api/products${timestamp}`);
Â  Â  Â  setProducts(productsRes.data);

      // --- YENÄ° AI Ã–NERÄ° Ä°STEÄÄ° BLOÄU ---
      // Bu bloÄŸu ayrÄ± bir try/catch iÃ§ine alÄ±yoruz ki,
      // AI servisi (Python) Ã§alÄ±ÅŸmÄ±yorsa bile ana menÃ¼ yÃ¼klenmeye devam etsin.
      try {
        // Test iÃ§in 'musteri_101' ID'sini gÃ¶nderiyoruz.
        // Bu, ai_service/data/etkilesimler.csv dosyanÄ±zdaki bir ID olmalÄ±.
        const customerData = {
          customer_id: 'musteri_101', 
          context: { time_of_day: 'evening' }
        };

        // Node.js'deki aiController'a (ve oradan Python'a) isteÄŸi at
        const recRes = await axios.post(
          `/api/ai/customer-recommendation/${restaurantId}`,
          customerData
        );

        // Gelen Ã¶nerileri state'e kaydet
        setRecommendations(recRes.data);
