import React from 'react';
import { Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';
import AdminPanel from './components/AdminPanel';
import Header from './components/Header';
import MenuPage from './components/MenuPage';

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/menu/:restaurantId" element={<MenuPage />} />
          <Route path="/admin" element={<AdminPanel />} />
          <Route path="/" element={<HomePage />} />
        </Routes>
      </div>
    </Router>
  );
}

// Ana sayfa bileşeni
function HomePage() {
  return (
    <div className="container">
      <div className="hero">
        <h1>QR Kod Menü Sistemi</h1>
        <p>Restaurant menünüzü dijital hale getirin ve müşterilerinize modern bir deneyim sunun!</p>
        <div className="hero-buttons">
          <button className="btn btn-primary">Demo Menü</button>
          <button className="btn btn-secondary">Yönetim Paneli</button>
        </div>
      </div>
    </div>
  );
}

export default App;
