import axios from 'axios';
import { Filter, Search } from 'lucide-react';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './MenuPage.css';

function MenuPage() {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    fetchData();
  }, [restaurantId]);

  const fetchData = async () => {
    try {
      setLoading(true);

      // Restaurant bilgilerini al
      const restaurantRes = await axios.get(`/api/restaurants/${restaurantId}`);
      setRestaurant(restaurantRes.data);

      // Kategorileri al
      const categoriesRes = await axios.get('/api/categories');
      setCategories(categoriesRes.data);

      // Ürünleri al
      const productsRes = await axios.get('/api/products');
      setProducts(productsRes.data);

    } catch (error) {
      console.error('Veri yükleme hatası:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filtrelenmiş ürünler
  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Menü yükleniyor...</div>
      </div>
    );
  }

  if (!restaurant) {
    return (
      <div className="container">
        <div className="error">Restaurant bulunamadı!</div>
      </div>
    );
  }

  return (
    <div className="menu-page">
      <div className="container">
        {/* Restaurant Bilgileri */}
        <div className="restaurant-info">
          {restaurant.logo && (
            <img src={restaurant.logo} alt={restaurant.name} className="restaurant-logo" />
          )}
          <div className="restaurant-details">
            <h1>{restaurant.name}</h1>
            {restaurant.description && <p>{restaurant.description}</p>}
            {restaurant.address && <p className="address">{restaurant.address}</p>}
          </div>
        </div>

        {/* Arama ve Filtre */}
        <div className="search-filter">
          <div className="search-box">
            <Search size={20} />
            <input
              type="text"
              placeholder="Ürün ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="category-filter">
            <Filter size={20} />
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="all">Tüm Kategoriler</option>
              {categories.map(category => (
                <option key={category._id} value={category._id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Ürünler */}
        <div className="products-grid">
          {filteredProducts.length === 0 ? (
            <div className="no-products">
              <p>Ürün bulunamadı.</p>
            </div>
          ) : (
            filteredProducts.map(product => (
              <ProductCard key={product._id} product={product} />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

// Ürün Kartı Bileşeni
function ProductCard({ product }) {
  return (
    <div className="product-card">
      {product.image && (
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>
      )}
      <div className="product-info">
        <h3>{product.name}</h3>
        {product.description && <p>{product.description}</p>}
        <div className="product-price">
          <span className="price">{product.price} ₺</span>
        </div>
      </div>
    </div>
  );
}

export default MenuPage;
